import React, { Component } from 'react'

export default class App extends Component {
	constructor() {
		super()

		this.state = {
			page: 1,
			firstname: '',
			lastname: '',
			password: '',
			repeatPassword: '',
			gender: 'male',
			errors: {
				firstname: false,
				lastname: false,
				password: false,
				repeatPassword: false,
				gender: false,
			},
		}
	}

	onChange = e => {
		this.setState({
			[e.target.name]: e.target.value,
		})
  };

  onSubmit = e => {
    const errors = {};
    if (this.state.firstname.length < 5) {
      errors.firstname = "Must be 5 characters or more"
    }

    if (this.state.lasttname.length < 5) {
      errors.lastname = "Must be 5 characters or more"
    }

    if (this.state.lasttname.length < 5) {
      errors.lastname = "Must be 5 characters or more"
    }

  }

	render() {
		const classNamePrevious = this.state.page === 1 ? true : false
		const classNameNext = this.state.page === 4 ? true : false

		return (
			<div className="form-container card">
				<form className="form card-body">
					<div className="form-group">
						<label>Firstname</label>
						<input
							type="text"
							name="firstname"
							className="form-control"
							placeholder="Enter firstname"
							value={this.state.firstname}
							onChange={this.onChange}
						/>
						{this.state.errors.firstname ? (
							<div className="invalid-value">{this.state.errors.firstname}</div>
						) : null}
					</div>
					<div className="form-group">
						<label>Lastname</label>
						<input
							type="text"
							name="lastname"
							className="form-control"
							placeholder="Enter lastname"
							value={this.state.lastname}
							onChange={this.onChange}
						/>
						{this.state.errors.lastname ? (
							<div className="invalid-value">{this.state.errors.lastname}</div>
						) : null}
					</div>
					<div className="form-group">
						<label>Password</label>
						<input
							type="password"
							name="password"
							className="form-control"
							placeholder="Enter password"
							value={this.state.password}
							onChange={this.onChange}
						/>
						{this.state.errors.password ? (
							<div className="invalid-value">{this.state.errors.password}</div>
						) : null}
					</div>
					<div className="form-group">
						<label>Repeat password</label>
						<input
							type="password"
							name="repeatPassword"
							className="form-control"
							placeholder="Enter repeat password"
							value={this.state.repeatPassword}
							onChange={this.onChange}
						/>
						{this.state.errors.repeatPassword ? (
							<div className="invalid-value">
								{this.state.errors.repeatPassword}
							</div>
						) : null}
					</div>
					<fieldset className="form-group">
						<div>Gender</div>
						<div className="form-check">
							<input
								className="form-check-input"
								type="radio"
								id="male"
								name="gender"
								value="male"
								checked={this.state.gender === 'male'}
								onChange={this.onChange}
							/>
							<label className="form-check-label" htmlFor="male">
								Male
							</label>
						</div>
						<div className="form-check">
							<input
								className="form-check-input"
								type="radio"
								id="female"
								name="gender"
								value="female"
								checked={this.state.gender === 'female'}
								onChange={this.onChange}
							/>
							<label className="form-check-label" htmlFor="female">
								Female
							</label>
						</div>
					</fieldset>
					<div className="form-group my-form-group">
						<button
							type="button"
							disabled={classNamePrevious}
							className="btn btn-light mr-4"
						>
							Previous
						</button>
						<button
							type="button"
							disabled={classNameNext}
							className="btn btn-secondary"
						>
							Next
						</button>
					</div>
				</form>
			</div>
		)
	}
}
